css:
.image-helper-view {
  padding: 16px;
}
.image-helper-view .splitter {
  width: 100%;
  height: 1px;
  margin-bottom: 16px;
  background-color: #31363f;
}
.image-helper-view .drop-area {
  text-align: center;
  height: 64px;
  border-style: dashed;
  cursor: pointer;
  margin-bottom: 32px;
  background-color: #23272e;
}
.image-helper-view .drop-area:hover {
  background-color: #31363f;
}
.image-helper-view .drop-area p {
  margin: 0;
  position: relative;
  top: 50%;
  transform: translateY(-50%);
}
.image-helper-view .drop-area.uploader {
  margin-bottom: 12px;
}
.image-helper-view .uploader-choice {
  margin-bottom: 24px;
}
.image-helper-view .uploader-choice select {
  background-color: #1f2228;
  border-color: #3e4451;
  margin: 0 6px;
}
.image-helper-view .close-btn,
.image-helper-view .add-btn {
  margin-right: 16px;
  width: 64px;
  text-align: center;
  padding: 0;
}
