css:
.auto-cursor {
  cursor: default;
}
@keyframes collapser-appears {
  0% {
    width: 0;
    display: none;
  }
  1% {
    width: 0;
    display: block;
  }
  60% {
    width: 0;
    opacity: 0;
  }
  100% {
    width: 10px;
    opacity: 1;
  }
}
@keyframes pane-appears {
  0% {
    opacity: 0;
    display: none;
  }
  1% {
    opacity: 0;
    display: block;
  }
  60% {
    opacity: 0;
  }
  100% {
    opacity: 1;
  }
}
kite-active-search {
  display: flex;
  flex-direction: column;
  background: #393d45;
  position: absolute;
  z-index: 100;
  box-shadow: -5px 0 10px rgba(0, 0, 0, 0.2);
  overflow: hidden;
  padding: 5px;
  transition: width 0.2s;
}
kite-active-search[position="hidden"] {
  display: none;
}
kite-active-search[position="top-right"] {
  top: 10%;
  right: 1px;
  padding-left: 10px;
}
kite-active-search[position="top-right"] .collapser {
  left: 0;
}
kite-active-search[position="bottom-right"] {
  bottom: 10%;
  right: 1px;
  padding-left: 20px;
}
kite-active-search[position="bottom-right"] .collapser {
  left: 0;
}
kite-active-search.collapsed {
  width: 3.2em;
  height: 3.2em;
  transition: width 0.1s, height 0.01s ease 0.1s;
}
kite-active-search.collapsed kite-expand,
kite-active-search.collapsed .select-list,
kite-active-search.collapsed .collapser {
  display: none;
}
kite-active-search h4 .sticky.fixed {
  background: #393d45;
}
kite-active-search kite-navigable-stack section.summary.collapsible.collapse.overflow .description::after {
  background-image: linear-gradient(to top, #393d45 0%, transparent 100%);
}
kite-active-search:not(.collapsed) {
  width: 400px;
  height: 80%;
}
kite-active-search:not(.collapsed) .expander {
  pointer-events: none;
}
kite-active-search:not(.collapsed) kite-expand,
kite-active-search:not(.collapsed) .select-list {
  animation: pane-appears 0.5s;
}
kite-active-search:not(.collapsed) .collapser {
  animation: collapser-appears 0.5s;
}
kite-active-search atom-text-editor[mini] {
  background: transparent;
  border-radius: 0;
  border-top: none;
  border-left: none;
  border-right: none;
  border-color: #282c34;
  font-family: sans-serif;
}
kite-active-search atom-text-editor[mini].is-focused {
  background: transparent;
  border-radius: 0;
  border-top: none;
  border-left: none;
  border-right: none;
  box-shadow: none;
}
kite-active-search .expander {
  padding: 0.4em;
  cursor: pointer;
  position: absolute;
  right: 5px;
  top: 5px;
}
kite-active-search .collapser {
  background: #54575f;
  color: #282c34;
  position: absolute;
  width: 10px;
  top: 0;
  bottom: 0;
  cursor: pointer;
}
kite-active-search .collapser::before {
  position: absolute;
  top: 50%;
  margin-top: -0.5em;
  left: 50%;
  margin-left: -3px;
}
kite-active-search kite-expand {
  flex: 1 1 auto;
  border-top: 1px solid #282c34;
  max-height: none;
  margin: 10px 5px 0;
}
kite-active-search .select-list.popover-list {
  width: auto;
  background: none;
  box-shadow: none;
}
kite-active-search .select-list.popover-list .list-group li {
  font-size: 1.2em;
  cursor: pointer;
}
kite-active-search .select-list.popover-list .list-group li.selected {
  background: rgba(215, 218, 224, 0.1);
}
