css:
.hydrogen.marker {
  display: flex;
}
.hydrogen code {
  color: inherit;
  background: inherit;
  font-size: inherit;
  font-family: inherit;
  padding: 0;
  white-space: pre;
}
.hydrogen img {
  background-color: white;
}
.hydrogen img,
.hydrogen svg,
.hydrogen video,
.hydrogen audio {
  display: block;
}
.hydrogen td,
.hydrogen th {
  border: 1px solid #61666f;
  padding: 0.35em 0.7em;
}
.hydrogen svg:first-child {
  background-color: white;
}
.hydrogen .MathJax_SVG_Display {
  margin: 0;
}
.hydrogen .MathJax_SVG .noError {
  padding: 0;
  border: none;
  color: #d55960;
}
.hydrogen .MathJax_SVG svg {
  background-color: unset;
  margin: 0 !important;
  display: inline;
}
.hydrogen .nteract-display-area-stderr {
  color: #d55960;
}
.hydrogen .inline-container {
  color: #c8ccd5;
  background: #3e4451;
  border-radius: 3px;
  box-shadow: 0 0 3px 0 rgba(0, 0, 0, 0.3);
}
.hydrogen .inline-container .cell_display {
  padding: 0 0.25em 0 0.25em;
}
.hydrogen .inline-container.icon::before {
  font-size: 80%;
  width: 1.2em;
  margin: 0;
  text-align: center;
  height: 100%;
}
.hydrogen .multiline-container {
  display: flex;
  border: solid 1px #181a1f;
}
.hydrogen .multiline-container .cell_display {
  padding: 10px;
}
.hydrogen .multiline-container .cell_display .modebar-group {
  display: flex;
}
.hydrogen .multiline-container .toolbar {
  border-left: solid 1px #181a1f;
  display: flex;
  flex-direction: column;
}
.hydrogen .multiline-container .toolbar > :not(:first-child) {
  border-top: solid 1px #181a1f;
}
.hydrogen .multiline-container .toolbar .icon::before {
  margin: 0;
  width: 2.5em;
  line-height: 2.5em;
  height: 2.5em;
  cursor: pointer;
  text-align: center;
}
.hydrogen .multiline-container .toolbar .icon:hover {
  color: #c8ccd5;
}
.hydrogen .spinner {
  padding: 0 0.5em 0 0.5em;
}
.hydrogen .spinner > div {
  background-color: #c8ccd5;
  height: 100%;
  width: 2px;
  display: inline-block;
  margin-left: 1px;
  animation: stretchdelay 1.2s infinite ease-in-out;
}
.hydrogen .spinner .rect1 {
  margin-left: 0px;
}
.hydrogen .spinner .rect2 {
  animation-delay: -1.1s;
}
.hydrogen .spinner .rect3 {
  animation-delay: -1s;
}
.hydrogen .spinner .rect4 {
  animation-delay: -0.9s;
}
.hydrogen .spinner .rect5 {
  animation-delay: -0.8s;
}
@keyframes stretchdelay {
  0%,
  40%,
  100% {
    transform: scaleY(0.32);
  }
  20% {
    transform: scaleY(0.8);
  }
}
.hydrogen.watch-sidebar {
  width: 300px;
  min-width: 250px;
  overflow-y: overlay;
  padding: 10px;
}
.hydrogen.watch-sidebar .toolbar .language {
  display: inline-block;
  padding: 4px;
}
.hydrogen.watch-sidebar .toolbar .btn {
  float: right;
}
.hydrogen.watch-sidebar .watch-view {
  width: 100%;
}
.hydrogen.watch-sidebar .watch-view .watch-input {
  margin-top: 10px;
  padding: 3px;
  border-radius: 3px;
  background-color: #282c34;
  font-size: 12px;
  min-height: 3em;
  border: 1px solid #181a1f;
}
.hydrogen.watch-sidebar .watch-view .slider {
  padding: 3px 22px;
  height: 27px;
  position: relative;
}
.hydrogen.watch-sidebar .watch-view .slider .rangeslider {
  position: relative;
  width: 100%;
  max-width: 100%;
}
.hydrogen.watch-sidebar .watch-view .slider .rangeslider .rangeslider__handle {
  cursor: pointer;
  display: block;
  position: absolute;
  width: 21px;
  height: 21px;
  border-radius: 3px;
  border: 1px solid #181a1f;
  background: #353b45;
}
.hydrogen.watch-sidebar .watch-view .slider .rangeslider .rangeslider__handle:hover {
  background: #3a3f4b;
}
.hydrogen.watch-sidebar .watch-resize-handle {
  position: absolute;
  top: 0px;
  bottom: 0px;
  left: -5px;
  width: 10px;
  cursor: ew-resize;
}
.hydrogen.watch-sidebar .btn-group {
  margin-top: 10px;
  display: block;
}
.hydrogen.watch-sidebar .btn-group .btn {
  width: 50%;
}
.hydrogen.watch-sidebar .multiline-container {
  width: 100%;
  max-height: 450px;
  overflow: auto;
  padding: 10px;
}
.hydrogen.watch-sidebar .multiline-container .modebar-group {
  display: flex;
}
.hydrogen.watch-sidebar video,
.hydrogen.watch-sidebar img,
.hydrogen.watch-sidebar svg {
  width: unset !important;
  height: unset !important;
  max-width: 100% !important;
  max-height: 450px !important;
}
.hidden {
  display: none;
}
.hydrogen.input-view .label {
  padding-bottom: 10px;
}
.hydrogen-panel-body {
  padding: 10px;
  overflow-y: auto;
  max-height: calc(100% - 2px - 32px);
}
.hydrogen-panel-body span[style] {
  color: #6494ed !important;
}
.hydrogen-panel-body code {
  color: inherit;
  white-space: pre;
  padding: 0px;
  font-size: 12px;
  font-family: inherit;
  background: inherit;
}
